mapper.delete:
DELETE FROM follows
WHERE follower_id = #{followerId}
AND followee_id = #{followeeId}

mapper.select:
SELECT EXISTS(
SELECT 1 FROM follows
WHERE follower_id = #{followerId}
AND followee_id = #{followeeId}
)

SELECT follower_id
FROM follows
WHERE followee_id = #{userId}

    AND created_at < to_timestamp(#{cursorId} / 1000.0)

ORDER BY created_at DESC
LIMIT #{limit}

SELECT followee_id
FROM follows
WHERE follower_id = #{userId}

    AND created_at < to_timestamp(#{cursorId} / 1000.0)

ORDER BY created_at DESC
LIMIT #{limit}
